sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        payload: params
    } = event;
    const route = "registration_success";
    const fields = {"type":"driver"};
    api.emit('NAV_ITEM_SELECTED', {
        route,
        fields,
        params
    });
}
