sp_widget.client_script:
api.controller=function($scope,$rootScope,$timeout,spUtil) {
  /* widget controller */
  var c = this;

  c.emitMyChargingSession = function(){
	if(c.data.charge_session && c.data.charge_session !=''){
		//console.log(c.data.charge_session);
		$rootScope.$emit('my_charging_session',c.data.charge_session);
		console.log(['emitted!',c.data.charge_session]);
	}
  };

  c.liveUpdate = function(){
	console.log('LIVE: My Charging Session');
	//Watch the Charger table live
	if(c.data.rw.charger){
		// console.log('rw charger');
		spUtil.recordWatch($scope, c.data.constants.TABLE_CHARGERS, c.data.rw.charger, function(record){
			spUtil.update($scope);//.then(function(){});
		})
	};
	//Watch the Charge events table live
	// console.log('rw charge events');
	spUtil.recordWatch($scope, c.data.constants.TABLE_QUEUE, c.data.rw.queue, function(record) {
		spUtil.update($scope);//.then(function(){});
	});
  };

  $timeout(function(){
	//c.emitMyChargingSession();
	c.liveUpdate();
  });

  //Watch local charge_session obj
  $scope.$watch('data.charge_session',function(){
	c.emitMyChargingSession();
  });



};

sp_widget.css:
.my-charging-container {
