}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  var cu = new ChargerUtils();
  data.constants = cu.getConstants();
  const {TABLE_CHARGERS,TABLE_QUEUE} = data.constants;

	data.charge_session = {
		charger: '',
		details: {},
	};
	data.rw = {};

  data.charge_session.details.user = gs.getUserID();
  data.queue_filter = 'active=true^requested_by='+data.charge_session.details.user+'^requested_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()';

  var chargeSession = new GlideRecord(TABLE_QUEUE);
  chargeSession.get(cu.getChargeSessionID(data.queue_filter));

  if(chargeSession){
	//data.charge_session.details = chargeSession;
	for(prop in chargeSession){
		var returned = chargeSession.getDisplayValue(prop) ? chargeSession.getDisplayValue(prop) : chargeSession[prop];
		//console.log(prop+': '+ returned);
		if(returned){
			data.charge_session.details[prop] = {
				display: returned,
				label: chargeSession.getLabel(prop),
				value: chargeSession.getValue(prop),
			};
			//console.log(JSON.stringify(data.charge_session.details[prop]));
		}
	}
	data.charge_session.charger = chargeSession.getValue('charger');
	//console.log(data.charge_session.charger);
	if(data.charge_session.charger) data.rw.charger = 'sys_id='+data.charge_session.charger;
	data.rw.queue = data.queue_filter;
	data.widget = $sp.getWidget("charger-card",{charger_id:data.charge_session.charger});
  }

})();
