sp_widget.client_script:
api.controller=function($scope,$rootScope,$timeout,$window) {
  /* widget controller */
  var c = this;

  c.emitMyChargingSession = function(){
	//console.log('getting and emitting charge session')
	if(c.data.charge_session && c.data.charge_session !=''){
		//console.log(c.data.charge_session);
		$rootScope.$emit('my_charging_session',c.data.charge_session);
		//console.log('emitted!');
	} else {
		$timeout(function(){
			c.emitMyChargingSession();
		},4000);
	}
  };

  $timeout(function(){
	c.emitMyChargingSession();
  });



};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  var cu = new ChargerUtils();
  var constants = cu.getConstants();
  const {TABLE_CHARGERS,TABLE_QUEUE} = constants;
  var me = gs.getUserID();
  //var charger = new GlideRecord(TABLE_CHARGERS);
  var chargeSession = cu.getChargeSessionByUser(me,'in_progress');
  data.charge_session = chargeSession;
  if(chargeSession){
  	var charger = chargeSession.charger;
  	//var charger = cu.getChargerIDByUser(me,'in_progress').charger;
  	var _options = {charger_id:charger};
	data.widget = $sp.getWidget("charger-card",_options);
  }

})();
