sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

  c.activeTab = 'available';


  c.filterList = function(action){
	console.log(action);
	c.activeTab = action;		 
	c.data.filter = action;
	if(action == 'available'){
		c.data.list = c.data.list.filter(c => c.state.value == 'available');
	} else {
		c.data.list = c.data.widgets;
	}
	//reset page
	// c.data.prev = 0;
	// c.data.next = c.data.pagination;
	// data.curr_page = 1;
	// c.server.update();
  }
};

