sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

  //todo: create constants
  const TABLE_CHARGERS = 'x_snc_charger_charger';

  data.widgets = [];

  //todo: populate
  var w = {};

  var charger = new GlideRecord(TABLE_CHARGERS);

  charger.query();

  while(charger._next()){
	var options = {charger_id:charger.getUniqueValue()};
	//var available = charger.getValue('state') == 'available'
	var state = $sp.getField(charger,'state');
	data.widgets.push({widget:$sp.getWidget("charger-card",options),state:state});
  }
data.list = data.widgets;

})();
