sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.css:
.charge-card-list {
  border: 1px solid grey;
  border-radius: 2rem;
  display: flex;
  flex-direction: row;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

  //todo: create constants
  const TABLE_CHARGERS = 'x_snc_charger_charger';

  data.widgets = [];

  //todo: populate
  var w = {};

  var charger = new GlideRecord(TABLE_CHARGERS);
  charger.query();

  while(charger._next()){
	w.options = {charger_id:charger.getUniqueValue()};
	data.widgets.push($sp.getWidget("charger-card",w.options));
  }

})();
