sp_widget.client_script:
api.controller=function($scope,spUtil,spModal) {
  /* widget controller */
  var c = this;

  c.liveUpdate = function(){
	console.log('Updating list live with filter: '+filter);
	var filter = 'state='+c.data.filter;
	spUtil.recordWatch($scope, c.data.constants.TABLE_CHARGERS, filter, function(name) {
		//console.log([filter,name]);
		spUtil.update($scope).then(function(){

		// 	// $scope.server.update().then(function(){

		// 	// });

		});
  	});
  };

  c.request = function(charger_id){

	spModal.open({
		"title" : "Join the Queue",
			"widget" : "widget-sc-cat-item-v2",
			"widgetInput" : {
				sys_id: 'ef53f80c878d8a106e4b11f83cbb35b0',
				display_cart_on_right: false,
				auto_redirect: "false",
			},
			"buttons": [
				{label:'âœ” Confirm', primary: true}
			]
		});
  };

  c.activeTab = 'available';

  c.refresh = function(){
	spUtil.update($scope).then(function(e){	
	});
  }

  c.filterList = function(action){
	c.activeTab = action;		 
	c.data.filter = action;
	c.server.update();
	// if(action == 'available'){
	// 	c.data.list = c.data.list.filter(c => c.state.value == 'available');
	// } else {
	// 	c.data.list = c.data.widgets;
	// }
  };


  if(c.data.options.live_update == 'true'){
	console.log('Updating live');
  	c.liveUpdate();
  }
};

