sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.options = options;
	var cu = new ChargerUtils();
	data.constants = cu.getConstants();
	const {TABLE_CHARGERS,TABLE_QUEUE} = data.constants;

  data.widgets = [];



  //todo: populate
  var w = {};
  var _filter = '';
  if(input && input.filter){
	//console.log('got input filter '+input.filter)
	_filter = input.filter;
  } else {
	_filter = data.options.filter;
  }
  if(_filter == 'all'){_filter = '';}

  var charger = new GlideRecord(TABLE_CHARGERS);
  if(_filter !== ''){
	//console.log('filtering '+_filter);
	charger.addQuery('state',_filter);
  }
  charger.query();

  while(charger._next()){
	var _options = {charger_id:charger.getUniqueValue(),click_action:data.options.click_action};
	//var available = charger.getValue('state') == 'available'
	var state = $sp.getField(charger,'state');
	data.widgets.push({widget:$sp.getWidget("charger-card",_options),state:state,charger_id:_options.charger_id});
  }
data.filter = _filter;
data.list = data.widgets;

})();
