sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;


  c.activeTab = 'available';


  c.filterList = function(action){
	c.activeTab = action;		 
	c.data.filter = action;
	c.server.update();
	// if(action == 'available'){
