sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  //todo: create constants
  //if(options){
	data.options = options;
	// data.option_type = 'options';
  //}
//   if(input){
// 	data.options.push(...data.options,input);
// 	data.option_type += '&input';
//   }
 console.log([data.option_type,data.options]);
  const TABLE_CHARGERS = 'x_snc_charger_charger';

  data.widgets = [];

  //todo: populate
  var w = {};
  var _filter = '';
  if(input && input.filter){
	_filter = input.filter;
  } else {
	_filter = data.options.filter;
  }

	switch(data.options.filter){
		case 'all':
			_filter = '';
		break;
		case 'available':
			_filter = data.options.filter;
		break;
		case 'charging':
			_filter = data.options.filter;
		break;
	}

  var charger = new GlideRecord(TABLE_CHARGERS);
  if(_filter && _filter !== ''){
	console.log('filtering '+_filter);
	charger.addQuery('state',_filter);
  }
  charger.query();

  while(charger._next()){
	var _options = {charger_id:charger.getUniqueValue()};
	//var available = charger.getValue('state') == 'available'
	var state = $sp.getField(charger,'state');
	data.widgets.push({widget:$sp.getWidget("charger-card",_options),state:state});
  }
data.list = data.widgets;

})();
