sp_widget.client_script:
api.controller=function($scope,spUtil,$rootScope) {
  /* widget controller */
  var c = this;

  $rootScope.$on('my_charging_session',function(event,parms){
	//console.log([event,parms]);
	c.data.charge_session = {sys_id:parms.charge_event,state:parms.state};
	c.data.charge_session_exists = (parms.charge_event && parms.charge_event != '');
	console.log(c.data.charge_session);
  });

  spUtil.recordWatch($scope, c.data.constants.TABLE_CHARGERS, 'state=available', function(name) {
		console.log([name]);
		spUtil.update($scope).then(function(){
		// 	// $scope.server.update().then(function(){
		});
	});
};

