sp_widget.client_script:
api.controller=function($scope,spUtil,$rootScope) {
  /* widget controller */
  var c = this;

  $rootScope.$on('my_charging_session',function(event,parms){
	console.log([event,parms]);
	c.data.charge_session = (parms.charge_event && parms.charge_event != '');
  });
};

sp_widget.css:
.charge-queue-container {
  border: 1px solid grey;
  border-radius: 2rem;
  display: flex;
  padding: 2rem;
  margin: 2rem;
}

.column {
  flex-direction: column;
}

.row {
  flex-direction: row;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var cu = new ChargerUtils();
	var constants = cu.getConstants();
	const {TABLE_CHARGERS,TABLE_QUEUE} = constants;

	var me = gs.getUserID();
	data.me = me;

	data.charge_session = false;
	//console.log(['me',me]);

	data.available_chargers = false;
	data.my_request = false;
	data.queue = [];

	var chargers = new GlideRecord(TABLE_CHARGERS);
	chargers.addQuery('state','available');
	chargers.query();
	if(chargers.hasNext()){
		data.available_chargers = true;

		var _options = {filter:'available',embedded:true,show_filter_buttons:false};
		//var available = charger.getValue('state') == 'available'
		data.widget = $sp.getWidget("charger-card-list",_options);
	} else {
		var queue = new GlideRecord(TABLE_QUEUE);
		//queue.addQuery('state','requested');
		//queue.addActiveQuery();
		queue.addEncodedQuery('active=true^state=requested^requested_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()');
		queue.orderBy('requested_on');
		queue.query();
		data.queue_length = queue.getRowCount();
		while(queue.next()){
			data.queue.push({sys_id:queue.getUniqueValue(),requested_by:queue.getValue('requested_by')});
			if(queue.getValue('requested_by') == me){
				data.my_request = true;
			}
		}

		// var my_request = new GlideRecord(TABLE_QUEUE);
		// my_request.addQuery('requested_by',me);
		// my_request.addQuery('state','requested');
		// my_request.addActiveQuery();
		// my_request.orderBy('requested_on');
		// my_request.query();
		// if(my_request.next()){
		// 	data.my_request = true;
		// }

	}
	data.my_queue = data.queue.findIndex(q => q.requested_by == data.me)+1;
})();
