sp_widget.client_script:
api.controller=function(spUtil,$scope,$rootScope,$timeout) {
  /* widget controller */
  var c = this;
  var options = c.options;
  $scope.loading = true;
    var inputs = c.data.input;
	//console.log(inputs);
	//console.log($scope);

	$timeout(function(){
		spUtil.get('sc-cat-item-with-input',inputs).then(function(response){
				c.cat_item = response;	
				$scope.loading = false;
		});
	});

};

sp_widget.css:
.loading-spinner {
	display: flex;
  	flex-direction: column;
    align-items: center;
  i {
  	animation: spin 1s infinite linear;
  }
}

@keyframes spin {
    from {
        transform:rotate(0deg);
    }
    to {
        transform:rotate(360deg);
    }
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  data.input = input;
  data.options = options;
})();
