sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	//console.log(options);
  //todo: create constants
  const TABLE_CHARGERS = 'x_snc_charger_charger';

  data.found = false;
  data.charger_id = '';
  data.charger = {};
  if(options.charger_id){
	data.charger_id = options.charger_id;
  }
  if(input && input.charger_id){
	data.charger_id = input.charger_id;
  }
	var charger = new GlideRecord(TABLE_CHARGERS);
	charger.get(data.charger_id);

	if(charger){
		data.fields = $sp.getFields(charger, 'active,description,id,last_update,location,name,number,service_status,state,sys_created_on,sys_id');
		for(i in data.fields){
			var field = data.fields[i];
			//console.log(field);
			data[field.name] = field;
		}

		var co = {};
		co.number = charger.getDisplayValue('number');
		co.description = charger.getDisplayValue('description');
		data.charger = co;
		data.found = true;
	} 

})();
