sp_widget.client_script:
api.controller=function($window,spModal) {
  /* widget controller */
  var c = this;

  c.action = function(action,data) {
	if(action == 'new_session'){
		spModal.open({
		"title" : "Start charge",
			"widget" : "sc-input-wrapper",
			"widgetInput" : {
				sys_id: c.data.constants.CAT_ITEM_NEW_SESSION,
				display_cart_on_right: false,
				auto_redirect: "false",
				passed_fields: data
			},
			"buttons": [
				{label:'âš¡ Confirm', primary: true},
				{label:'Cancel',cancel:true}
			]
		});
	}

  };
};

