sys_variable_value.value:
(function execute(inputs, outputs) {

  var userGR = new GlideRecord(inputs.table_name);
  userGR.addQuery('sys_id',inputs.record_sys_id);
  userGR.query();
  userGR.next();
  var email = userGR.email;
  var portalId = userGR.portal;
  var registrationConfigSysId = userGR.user_registration_config;

  //check if user is already processed for this registration configuration
  var isAlreadyProcessed = hasAlreadyProcessedWithProfile(email, registrationConfigSysId);
  var isSN = checkSN(email);
  var byteLength = 32;
  var tokenId = "";
  var instanceURL = gs.getProperty('glide.servlet.uri');
  var activationLink ="";
  var resetLink ="";

  if(isAlreadyProcessed || !isSN){

    //reject this registration request, as user is already registered with this registration config
    userGR.status.setDisplayValue("rejected");
    userGR.update();

    //prepare reset link for notifying user
    resetLink = instanceURL + "nav_to.do?uri=/$pwd_reset.do?sysparm_url=ss_default";

  } else{
    //Generate token and set with the registration record
    tokenId = GlideSecureRandomUtil.getSecureRandomString(byteLength);
    userGR.activation_token.setDisplayValue(tokenId);
    userGR.status.setDisplayValue("pending");
    userGR.update();  

    //prepare activation link
    activationLink = instanceURL + "api/now/v1/extuseronboarding?token_id=" + tokenId + "&portal_id=" + portalId +"&request_id="+inputs.record_sys_id;

     //delete old unprocessed requests
    deleteOldPendingRegRequests(email, portalId);

  }

  //prepare Masked Email
  var splitEmail = email.split('@');
  var firstLetter = splitEmail[0].substring(0,1);
  var lastLetter = splitEmail[0].substring(splitEmail[0].length - 1);
  var mask = splitEmail[0].substring(1, splitEmail[0].length - 1).replace(/[a-zA-Z0-9]/g,"*");
  var maskedEmail = firstLetter + mask + lastLetter + "@" + splitEmail[1];

  var Parameters = {"isexisting": isAlreadyProcessed,
                    "isSN": isSN,
    				        "resetlink":resetLink,
                    "maskedemail":maskedEmail,
                    "activationlink":activationLink
                    };

  var parser = new global.JSON(); 
  var formattedDetails = parser.encode(Parameters); 

  //add event in queue for sending appropriate notification
  gs.eventQueue('email.verification.for.external.users', userGR, email , formattedDetails);

  function checkSN(email){
    var splitEmail = email.split('@');
    return splitEmail[1] == 'servicenow.com';
  }

  //check if the user is already registered with this registration config
  function hasAlreadyProcessedWithProfile(email, registrationConfigSysId){
    //first check if user with this email is present in the sys_user table
    if(!isExistingUser(email))
      return false;

    //get the user record assocaited with this email
    var sysUserGR = new GlideRecord("sys_user");
    sysUserGR.addEncodedQuery('email='+email+'^ORuser_name='+email);
    sysUserGR.query();//NoDomain();
    var userSysIdList = "";
    while(sysUserGR.next()){
      userSysIdList+=sysUserGR.sys_id;
      userSysIdList+=",";
    }

    //get the user table associated with the this registration configuration
    var userTableName = getRegistrationConfigUserTableName(registrationConfigSysId);

    //check if user is already processed with this registration configuration 
    var gr = new GlideRecord(userTableName);
      if(!gr.isValid())
        return false;
      gr.addQuery("user", 'IN', userSysIdList);
      gr.query();//NoDomain();
      if(gr.next())
        return true;
      return false;
  }

  function isExistingUser(email, registrationConfigSysId){
    var sysUserGR = new GlideAggregate("sys_user");
    sysUserGR.addEncodedQuery('email='+email+'^ORuser_name='+email);
    sysUserGR.addAggregate('COUNT');
    sysUserGR.query();//NoDomain();
    return sysUserGR.next() && sysUserGR.getAggregate('COUNT') > 0;
  }

  /*
    return user table associated the registration profile
  */
  function getRegistrationConfigUserTableName(registrationConfigSysId){
    var gr = new GlideRecord("sys_user_registration_config");
    if(gr.isValid()){
      gr.addQuery('sys_id', registrationConfigSysId);
      gr.query();
      if(gr.next())
        return gr.user_table;
    }
  }

  /*
    if user already has pending registration request(s)
    and has registered again, delete old pending request(s).
  */
  function deleteOldPendingRegRequests(email, portal){
    var pendingRequestGr = new GlideRecord(inputs.table_name);
    pendingRequestGr.addQuery("email",email);
    pendingRequestGr.addQuery("status", 'pending');
    pendingRequestGr.addQuery("portal", portal);
    pendingRequestGr.query();
    while(pendingRequestGr.next()){
      if(pendingRequestGr.getUniqueValue() !== inputs.record_sys_id){
         pendingRequestGr.deleteRecord();
      }
    }
  }

})(inputs, outputs);

